function extractTaskData(html) {
    // Match module headers to get module names
    const moduleRegex = /<tr[^>]*class="cu-tableHeaderSecondary"[^>]*>[\s\S]*?<input[^>]*id="Modules_(\d+)__ModuleID"[^>]*value="(\d+)"[^>]*>([\s\S]*?)<\/td>/g;

    // Match task rows to get task IDs and names
    const taskRegex = /<input[^>]*id="Modules_(\d+)__Tasks_(\d+)__TaskID"[^>]*value="(\d+)"[^>]*>[\s\S]*?<input[^>]*id="\d+"[^>]*name="Modules\[\d+\]\.Tasks\[\d+\]\.IsScheduledTask"[^>]*>([\s\S]*?)<\/label>/g;

    // Match date inputs
    const dateRegex = /<input[^>]*id="Modules_(\d+)__Tasks_(\d+)__(StartDate|EndDate)"[^>]*value="([^"]*)"[^>]*>/g;

    // Store modules separately
    const modules = {};
    let moduleMatch;

    // Extract modules and their names
    while ((moduleMatch = moduleRegex.exec(html)) !== null) {
        const moduleIndex = moduleMatch[1];
        const moduleId = moduleMatch[2];
        const moduleName = moduleMatch[3].trim();

        modules[moduleIndex] = {
            moduleId: moduleId,
            moduleName: moduleName
        };
    }

    // Create flat list of tasks
    const tasks = [];
    let taskMatch;

    // Extract tasks and their names
    while ((taskMatch = taskRegex.exec(html)) !== null) {
        const moduleIndex = taskMatch[1];
        const taskIndex = taskMatch[2];
        const taskId = taskMatch[3];
        const taskNameHtml = taskMatch[4];

        // Extract just the text from the task name HTML
        const taskName = taskNameHtml.replace(/<[^>]*>/g, '').trim();

        if (modules[moduleIndex]) {
            const task = {
                projectId : $('Turn to a list1').item.json.data.id,
                moduleId: modules[moduleIndex].moduleId,
                moduleName: modules[moduleIndex].moduleName,
                taskId: taskId,
                taskName: taskName
            };
            tasks.push(task);
        }
    }

    // Add date information to tasks
    let dateMatch;
    while ((dateMatch = dateRegex.exec(html)) !== null) {
        const moduleIndex = dateMatch[1];
        const taskIndex = dateMatch[2];
        const dateType = dateMatch[3].toLowerCase(); // startdate or enddate
        const dateValue = dateMatch[4];

        // Find the corresponding task
        const task = tasks.find(t => 
            t.moduleId === modules[moduleIndex]?.moduleId && 
            t.taskId === html.match(new RegExp(`id="Modules_${moduleIndex}__Tasks_${taskIndex}__TaskID"[^>]*value="(\\d+)"`))?.[1]
        );

        if (task) {
            const dateObject = new Date(dateValue);
            if(dateValue) {
                task[dateType] = dateObject;
            }
        }
    }

    return {"data" : tasks};
}

