function extractJsonSimple(text) {
  const jsonRegex = /(\{[^}{]*\}|\[[^\[\]]*\])/g;
  const matches = text.match(jsonRegex) || [];

  return matches
    .map(match => {
      try {
        return JSON.parse(match);
      } catch (e) {
        return null;
      }
    })
    .filter(Boolean);
}



